import React,{useState} from 'react'
import { HeroContainer,HeroBg,VideoBg,HeroContent,HeroH1,HeroH2,HeroP1,HeroP,HeroButtonWrapper,ArrowForward,ArrowRight} from './HeroComponents';
import Video from '../../videos/video.mp4'
import { Button } from './ButtonElement';
 const Hero = () => {
    const [hover,setHover] = useState(false)
    const onHover = () =>{
        setHover(!hover)
    }
  return (
    <>
    <HeroContainer>
    <HeroBg>
        <VideoBg autoPlay loop muted src = {Video} type = 'video/mp4' /> 
    </HeroBg>
    <HeroContent>
        <HeroH1>LIVE MUSIC</HeroH1>
        <HeroH2>EVENTS EVERY DAY</HeroH2>
        <HeroP1>WEEKLY LINEUP:</HeroP1>
        <HeroP>SUNDAY - SANDHILL SUNDAY JAM! NEW ACTS WEEKLY & FREE POOL</HeroP>
        <HeroP>MONDAY - OPEN MIC WITH KEVIN CRANE & FREE POOL!</HeroP>
        <HeroP>TUESDAY - TRIVIA & GAME NIGHT WITH LEAH RANTZ!</HeroP>
        <HeroP>WEDNESDAY - KARAOKE WITH SHORTRYDES ENTERTAINMENT!</HeroP>
        <HeroP>THURSDAY - LIVE BLUEGRASS JAM</HeroP>
        <HeroP>FRIDAY - ORIGINAL ARTISTS LIVE!</HeroP>
        <HeroP>SATURDAY - LIVE LOCAL BANDS!</HeroP>
    </HeroContent>
    <HeroButtonWrapper>
        <Button to="about" onMouseEnter = {onHover} onMouseLeave = {onHover}>
            ABOUT{hover?<ArrowForward/>:<ArrowRight/>}
            </Button>
    </HeroButtonWrapper>
    </HeroContainer>
    </>
  );
};
export default Hero;
