import React,{useState} from 'react'
import { HeroContainer,HeroBg,VideoBg,Registration_box,Text_header,Text_message,Text_about,Text_education,Text_ed} from './HeroComponents';
import Video from '../../videos/video3.mp4'
import { Button } from './ButtonElement';
 const Hero = () => {
    const [hover,setHover] = useState(false)
    const onHover = () =>{
        setHover(!hover)
    }
  return (
    <>
    <HeroContainer>
    <HeroBg>
        <VideoBg autoPlay loop muted src = {Video} type = 'video/mp4' /> 
    </HeroBg>
    <Registration_box>
        <Text_header><h5> ~ % Welcome to Tarun's Webpage!</h5></Text_header>
        <Text_message><h5> ~ % Let's begin the adventure</h5></Text_message>
        <Text_about><h5> ~ % cd About</h5></Text_about>
        <Text_education><h5> ~ % cat Education</h5></Text_education>
        <Text_ed><h5> ~ % University of Massachusetts, Amherst (2019-2023)         D.P.S R.K.Puram</h5></Text_ed>
    </Registration_box>
    </HeroContainer>
    </>
  );
};
export default Hero;
