import styled from "styled-components";
import {MdArrowForward,MdKeyboardArrowRight} from 'react-icons/md'
import { keyframes } from 'styled-components'
import Image from '../../images/Vector.png'
export const HeroContainer = styled.div`

display:flex;
position:absolute;
justify-content:center;
align-items: center;
height: 900px;
width: 100vw;
top:8.5%;
left:0%;
z-index: 1;
:before{
    content:"";
    top:0%;
    left:0%;
    right:0%;
    bottom:0%;
    position:absolute;
    background: linear-gradient(180deg,rgba(0,0,0,0.5) 0%,rgba(0,0,0,0.4) 100%),linear-gradient(180deg,rgba(0,0,0,0.2)0%,transparent 100% );
};
`;
export const HeroBg = styled.div`
top:0;
right:0;
bottom:0;
left:0;
width: 100%;
height: 150%;
overflow: hidden;
`;
export const VideoBg = styled.video`
    width: 100%;
    height: 150%;
    -o-object-fit: cover;
    object-fit:cover;
    background: #232a34;
`;
