import styled from "styled-components";
import {MdArrowForward,MdKeyboardArrowRight} from 'react-icons/md'
export const HeroContainer = styled.div`
display:flex;
position:absolute;
justify-content:center;
align-items: center;
height: 800px;
width: 100vw;
top:8.5%;
left:0%;
z-index: 1;
:before{
    content:"";
    top:0%;
    left:0%;
    right:0%;
    bottom:0%;
    position:absolute;
    background: linear-gradient(180deg,rgba(0,0,0,0.5) 0%,rgba(0,0,0,0.4) 100%),linear-gradient(180deg,rgba(0,0,0,0.2)0%,transparent 100% );
};
`;
export const HeroBg = styled.div`
top:0;
right:0;
bottom:0;
left:0;
width: 100%;
height: 100%;
overflow: hidden;
`;
export const VideoBg = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit:cover;
    background: #232a34;
`;
export const HeroContent = styled.div`
z-index:3;
max-width : 1000px;
max-height : 1000px;
position: absolute;
left:2%;
padding: 20px 34px;
display:flex;
border-radius: 10px;
background-color:rgb(18 18 18);
flex-direction: column ;
box-shadow: 8px 8px 4px 1px rgb(255 193 7);
align-items:left;

`;
export const HeroH1 = styled.h1`
color: rgb(255 193 7);
font-size:40px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:18px;
};
@media screen and ( max-width:480px){
    font-size:10px;
};

`;
export const HeroH2 = styled.h2`
color: #fff;
font-size:40px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:18px;
};
@media screen and ( max-width:480px){
    font-size:10px;
};

`;
export const HeroP1 = styled.p`
color:rgb(255 193 7) ;
font-size:16px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:12px;
};
@media screen and ( max-width:480px){
    font-size:10px;
};

`;
export const HeroP = styled.p`
color:#fff ;
font-size:16px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:12px;
};
@media screen and ( max-width:480px){
    font-size:10px;
};

`;
export const HeroButtonWrapper = styled.div`
margin-top:32px;
display:flex;
flex-direction:column;
align-items:center;
`
export const ArrowForward = styled(MdArrowForward)`
margin-left:8px;
font-size:20px;
`
export const ArrowRight = styled(MdKeyboardArrowRight)`
margin-left:8px;
font-size:20px;
`
