import styled from "styled-components";
import {MdArrowForward,MdKeyboardArrowRight} from 'react-icons/md'
import { keyframes } from 'styled-components'
export const HeroContainer = styled.div`

display:flex;
position:absolute;
justify-content:center;
align-items: center;
height: 900px;
width: 100vw;
top:8.5%;
left:0%;
z-index: 1;
:before{
    content:"";
    top:0%;
    left:0%;
    right:0%;
    bottom:0%;
    position:absolute;
    background: linear-gradient(180deg,rgba(0,0,0,0.5) 0%,rgba(0,0,0,0.4) 100%),linear-gradient(180deg,rgba(0,0,0,0.2)0%,transparent 100% );
};
`;
export const HeroBg = styled.div`
top:0;
right:0;
bottom:0;
left:0;
width: 100%;
height: 100%;
overflow: hidden;
`;
export const VideoBg = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit:cover;
    background: #232a34;
`;
export const Registration_box  = styled.div`
    top:22.5%;
    position: absolute ;
    left:22.5%;
    display: block;
    background-image: url(Vector.png);
    height: 250px;
    width: 850px;
    justify-content: center;
    border-radius: 10px;
    box-shadow: 0 10px 10px 0 grey;
    animation: {width_inc} 0.5s steps(30) 2s forwards;
`
export const Text_header = styled.div`
    margin-top: 5%;
    margin-left: 5%;
    color: grey;
    position: relative;
    width: max-content;

    &:before{
        background: white;
        animation: {typewriter} 1s steps(29) forwards;
}
&:after{
        content : "";
        position: absolute;
        top:0;
        bottom: 0;
        left: 0;
        right: 0;
}
`


const typewriter = keyframes`
    to {
        left: 100%;
    }

`
const width_inc = keyframes`
    to {
        height: 450px;
    }
`
export const Text_message = styled.div`
    margin-top: 5%;
    margin-left: 5%;
    color: grey;
    position: relative;
    width: max-content;
    /* transform: translateY(3rem); */

    &:before,&:after{
    content : "";
    position: absolute;
    top:0;
    bottom: 0;
    left: 0;
    right: 0;
}
&:before{
    background: white;
    animation: ${typewriter} 1s steps(29) 1s forwards;
}`
export const ArrowForward = styled(MdArrowForward)`
margin-left:8px;
font-size:20px;
`
export const ArrowRight = styled(MdKeyboardArrowRight)`
margin-left:8px;
font-size:20px;
`
