import styled from "styled-components";

export const HeroContainer = styled.div`
display:flex;
justify-content:center;
align-items: center;
height: 800px;
width: 100vw;
z-index: 1;
`;
export const HeroBg = styled.div`
top:0;
right:0;
bottom:0;
left:0;
width: 100%;
height: 100%;
overflow: hidden;
`;
export const VideoBg = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit:cover;
    background: #232a34;
`;
export const HeroContent = styled.div`
z-index:3;
max-width : 500px;
max-height : 700px;
position: absolute;
left:10%;
padding: 8px 24px;
display:flex;
border-radius: 10px;
background-color:blue;
flex:direction: column;
align-items: center;

`;
export const HeroH1 = styled.h1`
color: #fff;
font-size:48px;
text-align: center;
@media screen and ( max-width:780px){
    font-size:48px;
};
@media screen and ( max-width:480px){
    font-size:32px;
};

`;
