import styled from "styled-components";

export const HeroContainer = styled.div`
display:flex;
justify-content:center;
align-items: center;
height: 800px;
width: 100vw;
z-index: 1;
`;
export const HeroBg = styled.div`
top:0;
right:0;
bottom:0;
left:0;
width: 100%;
height: 100%;
overflow: hidden;
`;
export const VideoBg = styled.video`
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit:cover;
    background: #232a34;
`;
export const HeroContent = styled.div`
z-index:3;
max-width : 800px;
max-height : 600px;
position: absolute;
left:5%;
padding: 10px 34px;
display:flex;
border-radius: 10px;
background-color:rgb(18 18 18);
flex-direction: column ;
box-shadow: 12px 5px 1px 1px rgb(255 193 7);
align-items:left;

`;
export const HeroH1 = styled.h1`
color: rgb(255 193 7);
font-size:28px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:28px;
};
@media screen and ( max-width:480px){
    font-size:10px;
};

`;
export const HeroH2 = styled.h2`
color: #fff;
font-size:28px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:28px;
};
@media screen and ( max-width:480px){
    font-size:10px;
};

`;
export const HeroP1 = styled.p`
color:rgb(255 193 7) ;
font-size:18px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:18px;
};
@media screen and ( max-width:480px){
    font-size:12px;
};

`;
export const HeroP = styled.p`
color:#fff ;
font-size:18px;
text-align: left;
@media screen and ( max-width:780px){
    font-size:12px;
};
@media screen and ( max-width:480px){
    font-size:12px;
};

`;
